import './pages.css';
import React from 'react';

import ScrollToTop from './scroll_to_top';

import apis from '../../api';

import findImage from '../helper.js';

class Split extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            teams: [],
            loading: true,
        };
    }

    componentDidMount = async () => {
        await apis.getTeamsByMap('Split').then(res => {
            this.setState({
                teams: res.data.data,
                loading: false,
            });
        });
    }

    render() {
        const { teams, loading } = this.state;

        if (loading) return null;

        return (
            <div>
                <ScrollToTop/>
                <ul>
                    {teams.map(item => {
                        return <li key = {item._id}>
                            {item.team.map(agent => {
                                return <img src = {findImage(agent)} className = "agent-img" alt = {agent} title = {agent}/>;
                            })}
                        </li>;
                    })}
                </ul>
            </div>
        );
    }
};

export default Split;
