import './pages.css';
import React from 'react';
import ScrollToTop from './scroll_to_top';

import apis from '../../api';

import findImage from '../helper.js';

class Ascent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            teams: [],
            loading: true,
        };
    }

    componentDidMount = async () => {
        await apis.getTeamsByMap('Ascent').then(res => {
            this.setState({
                teams: res.data.data,
                loading: false,
            });
        });
    }

    createList = () => {
        const teams = this.state.teams;
        const listItems = teams.map(item => {
            <div key = {item._id}>
                {item.team.map(agent => {
                    <img src = {findImage(agent)} className = "agent-img" alt = {agent} title = {agent}/>;
                })}
            </div>;
        })

        return listItems;

    }

    render() {
        const { loading } = this.state;

        if (loading) return null;

        return (
            <div className = "teams-container">
                <ScrollToTop/>
                {this.createList()}
            </div>
        );
    }
};

export default Ascent;
