import './pages.css';
import React from 'react';
import Rankings from './rankings';
import withRouter from 'react-router-dom';

import apis from '../../api';

import findImage from '../helper.js';

class Template extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            teams: [],
            loading: true,
            left: false,
            right: false,
        };
    }

    componentDidMount = async () => {
        await apis.getTeamsByMap(this.props.map).then(res => {
            this.setState({
                teams: res.data.data,
                loading: false,
            });
        });

        window.addEventListener("keydown", (e) => {
            switch (e.key) {
                case "ArrowLeft":
                    this.setState({
                        left: true,
                    })
                    break;
                case "ArrowRight":
                    this.setState({
                        right: true,
                    })
                    break;
            }
        })
    }

    createList = () => {
        const teams = this.state.teams;
        const listItems = teams.map(item => {
            return (<div className = "team-card" key = {item._id}>
                {item.team.map((agent, index) => {
                    return (<img src = {findImage(agent)} className = "agent-img" alt = {agent} title = {agent} key = {index}/>);
                })}
            </div>);
        })

        return listItems;

    }

    handleArrows = (direction) => {
        if (direction == 'left') {
            switch (this.props.map) {
                case 'breeze':
                    return '/ascent';
                case 'icebox':
                    return '/breeze';
                case 'bind':
                    return '/icebox';
                case 'haven':
                    return '/bind';
                case 'split':
                    return '/haven';
                case 'ascent':
                    return '/split';
                default:
                    return '/breeze';
            }
        }
        else {
            switch (this.props.map) {
                case 'breeze':
                    return '/icebox';
                case 'icebox':
                    return '/bind';
                case 'bind':
                    return '/haven';
                case 'haven':
                    return '/split';
                case 'split':
                    return '/ascent';
                case 'ascent':
                    return '/breeze';
                default:
                    return '/breeze';
            }
        }
    }

    render() {
        const { loading, left, right } = this.state;

        if (loading) return null;

        return (
            <div className = "teams-container">
                {left && this.props.history.push(this.handleArrows('left'))}
                {right && this.props.history.push(this.handleArrows('right'))}
                <Rankings/>
                {this.createList()}
            </div>
        );
    }
};

export default withRouter(Template);
