import './scroll_to_top.css';
import React from 'react';

class ScrollToTop extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showButton: false,
        };
    }

    componentDidMount() {

        window.addEventListener("scroll", () => {
            if (window.pageYOffset > 50) {
                this.setState({
                    showButton: true,
                });
            } 
            else {
                this.setState({
                    showButton: false,
                });
            }
        });
        
    }

    componentWillUnmount() {

        window.removeEventListener("scroll", () => {
            if (window.pageYOffset > 50) {
                this.setState({
                    showButton: true,
                });
            } 
            else {
                this.setState({
                    showButton: false,
                });
            }
        });

    }

    scrollToTop = () => {
        window.scrollTo({
            top: 0,
            behavior: 'smooth',
        });
    };  

    render () {
        return (
            <div>
                {this.showButton && (
                    <button onClick={this.scrollToTop} className="back-to-top">
                        &#8679;
                    </button>
                )}
            </div>
        );
    }
}
